pragma solidity >=0.4.22 <0.6.0;

contract Tweeter {
  uint public latestTweetIndex;
  mapping(uint => string) tweets;
  mapping(string => address) tweeters;
  mapping(string => uint) upvotes;

  uint public latestDatabaseIndex = 0;
  mapping(uint => string) datebaseHashes;

  address oracle;

  event NewTweetEvent(uint currentDatabaseIndex, address tweeter, string ipfsHash);
  event NewUpvoteEvent(uint currentDatabaseIndex, string ipfsHash, uint upvotes);
  event DBUpdateEvent(uint currentDatabaseIndex);

  constructor() public {
    oracle = msg.sender;
  }

  modifier isOracle { require(oracle == msg.sender); _; }

  function tweet(string ipfsHash) public {
    tweets[latestTweetIndex] = ipfsHash;
    tweeters[ipfsHash] = msg.sender;
    latestTweetIndex += 1;
    emit NewTweetEvent(latestDatabaseIndex, msg.sender, ipfsHash);
  }

  function update(string ipfsHash) public {
    upvotes[ipfsHash] += 1;
    emit NewUpvoteEvent(latestDatabaseIndex, ipfsHash, upvotes[ipfsHash]);
  }

  function setNewDB(string ipfsHash) public isOracle {
    latestDatabaseIndex += 1;
    datebaseHashes[latestDatabaseIndex] = ipfsHash;
    emit DBUpdateEvent(latestDatabaseIndex);
  }

  function getTweeter(string ipfsHash) public view returns (address) {
    return tweeters[ipfsHash];
  }

  function getDB(uint currentDatabaseIndex) public view returns (string) {
    require(currentDatabaseIndex >= 0 &&  currentDatabaseIndex <= latestDatabaseIndex);
    return datebaseHashes[currentDatabaseIndex];
  }

  function getLatestDBIndex() public view returns (uint) {
    return latestDatabaseIndex;
  }

  function getLatestDBHash() public view returns (string) {
    return datebaseHashes[latestDatabaseIndex];
  }
}
